import React, { useEffect, useRef, useState } from "react";
const Journey = () => {
  const [animationProgress, setAnimationProgress] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [isPlaying, setIsPlaying] = useState(true);
  const timelineRef = useRef(null);
  const beeRef = useRef(null);

  // Handle animation
  useEffect(() => {
    let interval;

    if (isPlaying) {
      interval = setInterval(() => {
        setAnimationProgress(prev => {
          if (prev >= 100) {
            clearInterval(interval);
            return 100;
          }
          return prev + 0.5;
        });
      }, 50);
    }

    return () => clearInterval(interval);
  }, [isPlaying]);

  // Handle drag events
  const handleMouseDown = () => {
    setIsDragging(true);
    setIsPlaying(false);
  };

  const handleMouseMove = (e) => {
    if (!isDragging || !timelineRef.current) return;

    const timelineRect = timelineRef.current.getBoundingClientRect();
    const relativeX = e.clientX - timelineRect.left;
    let progress = (relativeX / timelineRect.width) * 100;

    // Constrain progress between 0 and 100
    progress = Math.max(0, Math.min(100, progress));
    setAnimationProgress(progress);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  // Handle touch events for mobile
  const handleTouchStart = () => {
    setIsDragging(true);
    setIsPlaying(false);
  };

  const handleTouchMove = (e) => {
    if (!isDragging || !timelineRef.current) return;

    const timelineRect = timelineRef.current.getBoundingClientRect();
    const touch = e.touches[0];
    const relativeX = touch.clientX - timelineRect.left;
    let progress = (relativeX / timelineRect.width) * 100;

    // Constrain progress between 0 and 100
    progress = Math.max(0, Math.min(100, progress));
    setAnimationProgress(progress);
  };

  const handleTouchEnd = () => {
    setIsDragging(false);
  };

  // Reset animation
  const resetAnimation = () => {
    setAnimationProgress(0);
    setIsPlaying(true);
  };

  const milestones = [
    { year: "1997", title: "Born", description: "Started my journey in this world" },
    { year: "2002", title: "Started School", description: "Began Grade 1 education" },
    { year: "2014", title: "Finished Class 10", description: "Completed secondary education" },
    { year: "2016", title: "Finished Class 12", description: "Completed higher secondary education" },
    { year: "2016", title: "Started University", description: "Began Bachelor's degree" },
    { year: "2020", title: "Graduated University", description: "Completed Bachelor's degree" },
    { year: "2020", title: "Started Working", description: "Began professional career" },
    { year: "2022", title: "Started Master's", description: "Began postgraduate studies" },
    { year: "2023", title: "Working Again", description: "Returned to professional work" }
  ];

  // Calculate position on zigzag path
  const getZigzagPosition = (progress) => {
    const x = progress;
    const y = 50 + 30 * Math.sin(progress * 0.1); // Zigzag effect
    return { x, y };
  };

  const beePosition = getZigzagPosition(animationProgress);

  return (
    <section className="journey-area" id="journey">
      <div className="container pt-5">
        <div className="row">
          <div className="col-lg-12 text-center services-title">
            <h1 className="text-uppercase title-text">My Journey</h1>
            <p className="para">
              Follow the bee along the path of my life's journey
            </p>
          </div>
        </div>

        <div className="controls">
          <button 
            className="control-btn" 
            onClick={() => setIsPlaying(!isPlaying)}
          >
            {isPlaying ? "Pause" : "Play"}
          </button>
          <button className="control-btn" onClick={resetAnimation}>
            Reset
          </button>
        </div>

        <div 
          className="journey-timeline" 
          ref={timelineRef}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseUp}
          onTouchMove={handleTouchMove}
          onTouchEnd={handleTouchEnd}
        >
          <svg className="zigzag-path" viewBox="0 0 100 100" preserveAspectRatio="none">
            <path 
              d="M 0,50 
                 C 10,30 20,70 30,50
                 C 40,30 50,70 60,50
                 C 70,30 80,70 90,50
                 C 95,40 100,60 100,50" 
              fill="none" 
              stroke="#ffcc00" 
              strokeWidth="0.5" 
              strokeDasharray="2,3"
            />
          </svg>

          <div 
            className="bee-container" 
            ref={beeRef}
            style={{ 
              left: `${beePosition.x}%`, 
              top: `${beePosition.y}%`,
              cursor: isDragging ? 'grabbing' : 'grab'
            }}
            onMouseDown={handleMouseDown}
            onTouchStart={handleTouchStart}
          >
            <div className="bee">
              <div className="bee-body">
                <div className="bee-wing"></div>
                <div className="bee-wing right"></div>
              </div>
            </div>
          </div>

          <div className="milestones-container">
            {milestones.map((milestone, index) => {
              const position = getZigzagPosition((index / (milestones.length - 1)) * 100);
              return (
                <div 
                  key={index} 
                  className="milestone"
                  style={{ 
                    left: `${position.x}%`, 
                    top: `${position.y}%` 
                  }}
                >
                  <div className="milestone-marker"></div>
                  <div className="milestone-content">
                    <h3>{milestone.year}</h3>
                    <h4>{milestone.title}</h4>
                    <p>{milestone.description}</p>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Journey;
