import React, { useState, useRef, useEffect } from 'react';
import './jou';

const JourneyTimeline = () => {
  const [currentPosition, setCurrentPosition] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);
  const timelineRef = useRef(null);
  const beeRef = useRef(null);

  const milestones = [
    { year: '2018', title: 'First Steps', description: 'Started our journey with a small team and big dreams.' },
    { year: '2019', title: 'Growth Phase', description: 'Expanded our services and reached new markets.' },
    { year: '2020', title: 'Adaptation', description: 'Pivoted during challenging times and found new opportunities.' },
    { year: '2021', title: 'Breakthrough', description: 'Launched our flagship product to great acclaim.' },
    { year: '2022', title: 'Expansion', description: 'Opened offices in three new countries.' },
    { year: '2023', title: 'Innovation', description: 'Introduced groundbreaking AI technology to our platform.' },
    { year: '2024', title: 'Future Vision', description: 'Setting new industry standards with our latest initiatives.' }
  ];

  // Calculate positions for the parabolic path
  const calculateParabolicPosition = (index, total) => {
    const x = (index / (total - 1)) * 100;
    // Higher parabola equation: y = a(x - h)^2 + k
    // Using a higher coefficient for more pronounced curve
    const y = 50 - 40 * Math.pow((x - 50) / 50, 2);
    return { x, y };
  };

  // Handle mouse events for dragging
  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.pageX - timelineRef.current.offsetLeft);
    setScrollLeft(timelineRef.current.scrollLeft);
  };

  const handleMouseLeave = () => {
    setIsDragging(false);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    e.preventDefault();
    const x = e.pageX - timelineRef.current.offsetLeft;
    const walk = (x - startX) * 2;
    timelineRef.current.scrollLeft = scrollLeft - walk;
  };

  // Handle touch events for mobile
  const handleTouchStart = (e) => {
    setIsDragging(true);
    setStartX(e.touches[0].pageX - timelineRef.current.offsetLeft);
    setScrollLeft(timelineRef.current.scrollLeft);
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;
    const x = e.touches[0].pageX - timelineRef.current.offsetLeft;
    const walk = (x - startX) * 2;
    timelineRef.current.scrollLeft = scrollLeft - walk;
  };

  // Update bee position based on scroll
  useEffect(() => {
    const handleScroll = () => {
      if (timelineRef.current && beeRef.current) {
        const scrollPercentage = timelineRef.current.scrollLeft / 
          (timelineRef.current.scrollWidth - timelineRef.current.clientWidth);
        setCurrentPosition(scrollPercentage);

        // Calculate bee position along the parabolic path
        const x = scrollPercentage * 100;
        const y = 50 - 40 * Math.pow((x - 50) / 50, 2);

        beeRef.current.style.left = `${x}%`;
        beeRef.current.style.top = `${y}%`;
      }
    };

    if (timelineRef.current) {
      timelineRef.current.addEventListener('scroll', handleScroll);
    }

    return () => {
      if (timelineRef.current) {
        timelineRef.current.removeEventListener('scroll', handleScroll);
      }
    };
  }, []);

  return (
    <div className="journey-area">
      <h2 className="title-text">Our Journey</h2>
      <p className="para">Follow our growth through the years with this interactive timeline</p>

      <div className="controls">
        <button className="control-btn" onClick={() => timelineRef.current.scrollBy({ left: -200, behavior: 'smooth' })}>
          ← Previous
        </button>
        <button className="control-btn" onClick={() => timelineRef.current.scrollBy({ left: 200, behavior: 'smooth' })}>
          Next →
        </button>
      </div>

      <div 
        className="journey-timeline"
        ref={timelineRef}
        onMouseDown={handleMouseDown}
        onMouseLeave={handleMouseLeave}
        onMouseUp={handleMouseUp}
        onMouseMove={handleMouseMove}
        onTouchStart={handleTouchStart}
        onTouchEnd={handleMouseUp}
        onTouchMove={handleTouchMove}
      >
        <div className="parabolic-path-container">
          <svg className="parabolic-path" viewBox="0 0 100 100" preserveAspectRatio="none">
            <path 
              d="M 0,50 Q 25,10 50,50 T 100,50" 
              fill="none" 
              stroke="#ffcc00" 
              strokeWidth="2" 
              strokeDasharray="5,5"
            />
          </svg>

          <div className="bee-container" ref={beeRef}>
            <div className="bee">
              <div className="bee-body"></div>
              <div className="bee-wing"></div>
              <div className="bee-wing right"></div>
            </div>
          </div>
        </div>

        <div className="milestones-container">
          {milestones.map((milestone, index) => {
            const position = calculateParabolicPosition(index, milestones.length);
            return (
              <div 
                key={index}
                className="milestone"
                style={{ left: `${position.x}%`, top: `${position.y}%` }}
              >
                <div className="milestone-marker">
                  <span className="milestone-year">{milestone.year}</span>
                </div>
                <div className="milestone-content always-visible">
                  <h4>{milestone.title}</h4>
                  <p>{milestone.description}</p>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      <p className="instructions">Drag the timeline to navigate or use the buttons above</p>
    </div>
  );
};

export default JourneyTimeline;
